label("$C$", C, S);
label("$D$", D, S);
label("$O$", O, N);
label("$P$", P, S);
label("$E$", E, N);
label("$F$", F, W);
label("$G$", G, N);
label("$H$", H, E);
label("$M$", M, N);
label("$O'$", O1, S);
label("$P'$", P1, S);
