label("$B$", B, SW);
label("$C$", C, SE);
dot("$G_2$", G[2], NE);
dot("$G_3$", G[3], W);
dot("$M_2$", M[2], S);
dot("$M_3$", M[3], S);
label("$P$", P, S);
