label("$1$",(O2+EE)/2,NE);
label("$A$",A,N);
label("$B$",B,SW);
label("$C$",C,SE);
label("$x$",(D + O_1)/2,NE);
label("$x$",(EE + O_2)/2,NE);
label("$x - 1$",(B + D)/2,S);
label("$x - 2$",(C + EE)/2,S);
