pair A=(0,80), B=(46,108), C=(100,80), D=(54,18), E=(19,0);
draw(A--C--E--B--D--cycle);
label("$A$", A, W);
label("$B$ ", B, N);
label("$C$", shift(7,0)*C);
label("$D$", D, SE);
label("$E$", E, SW);
label("$F$", (23,43));
label("$G$", (35, 86));
[/asy]

