A = (2,3);
B = (0,0);
C = (6,0);
D = interp(A,B,0.4);
E = interp(A,C,3/5);

draw(A--B--C--cycle);
draw(D--E);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, NW);
label("$E$", E, NE);
[/asy]

