A = (0,0);
B = (1.7,-0.4);
C = (1.5,1.5);
D = (-0.2,1.6);
Ap = 2*B - A;
Bp = 2*C - B;
Cp = 2*D - C;
Dp = 2*A - D;

draw(A--B--C--D--cycle);
draw(Ap--Bp--Cp--Dp--cycle);
draw(B--Bp);
draw(C--Cp);
draw(D--Dp);
draw(A--Ap);

label("$A$", A, SW);
label("$B$", B, SE);
label("$C$", C, NE);
label("$D$", D, NW);
label("$A'$", Ap, SE);
label("$B'$", Bp, NE);
label("$C'$", Cp, NW);
label("$D'$", Dp, SW);
