inner[0] = Circle(O[0],r);
inner[1] = Circle(O[1],r);
inner[2] = Circle(O[2],r);
inner[3] = Circle(O[3],r);
inner[4] = Circle(O[4],r);
inner[5] = Circle(O[5],r);

draw(outer);
