O[0] = (0,0);
A[0] = dir(60);
B[0] = dir(120);
M[0] = (A[0] + B[0])/2;
N[0] = foot(O[0],A[0],B[0]);

draw(Circle(O[0],30));
draw(Circle(A[0],30 - 2*sqrt(3)));
draw(Circle(B[0],30 - 2*sqrt(3)));
