A = (0,0,0);
B = (0,12*sqrt(3),0);
C = (13*sqrt(3),12*sqrt(3),0);
D = (13*sqrt(3),0,0);
P = (13*sqrt(3)/2,12*sqrt(3)/2,5);

draw(B--C--P--D--C);
draw(D--A--P,dashed);
