draw((-11,0)--(11,0));

for (i = -10; i <= 10; ++i) {
  draw((i,-0.2)--(i,0.2));
}

label("$a$", (0,-0.2), S);
label("$b$", (-2,-0.2), S);
label("$b$", (2,-0.2), S);
label("$c$", (-5,-0.2), S);
label("$c$", (-1,-0.2), S);
label("$c$", (1,-0.2), S);
label("$c$", (5,-0.2), S);
label("$d$", (-9,-0.2), S);
label("$d$", (-5,-0.8), S);
label("$d$", (-3,-0.2), S);
label("$d$", (-1,-0.8), S);
label("$d$", (1,-0.8), S);
label("$d$", (3,-0.2), S);
label("$d$", (5,-0.8), S);
label("$d$", (9,-0.2), S);
