int i;
for (i=1; i<=100; ++i) {
if (i%3 != 0) {
draw((i,-5)--(i,5));
}}
draw((0,-5)--(100,-5));
draw((0,5)--(100,5));
label("$1$",(1,-5),S);
label("$2$",(2,-5),S);
label("$3$",(3,-5),S);
label("$4$",(4,-5),S);
