label("A",10dir(240),W);
label("B",10dir(300),E);
label("C",10dir(0),E);
label("D",10dir(300)+(0,-10),E);

draw(10dir(300)+2dir(-135)--10dir(300)+dir(-135),linewidth(2));
[/asy]

