[asy]
pair A,B,C,D;
A=(0,0);
B=(5,0);
C=(5,3);
D=(2,1);
draw(A--B--C--A);
draw(D--B);
label("$A$",A,SW);
label("$B$",B,SE);
label("$C$",C,N);
