label("$\begin{pmatrix} 4 \\ 5 \end{pmatrix}$", A, N);
label("$\begin{pmatrix} 2 \\ 0 \end{pmatrix}$", B, S);
label("$\operatorname{proj}_{\begin{pmatrix} 2 \\ 0 \end{pmatrix}} \begin{pmatrix} 4 \\ 5 \end{pmatrix}$", P, N);
label("$k \begin{pmatrix} 2 \\ 0 \end{pmatrix}$", P/2, W);
label("$k$", (2,0.25));
label("$y$", (2,0), S);
