draw((6,0)--(9,112^.5)--(0,0));
label("$A$", (0,0), SW);
label("$B$", (3,112^.5), N);
label("$C$", (6,0), SE);
label("$D$", (4.75,6), N);
label("$E$", (9,112^.5), N);
[/asy]

