unitsize(0.8 cm);

pair A, B, O;

A = (3,1);
B = (-5,2);
O = (0,0);

draw(O--A,Arrow(6));
draw(O--B,Arrow(6));
draw(A--B--(A + B)--cycle,dashed);
draw((-6,0)--(4,0));
draw((0,-1)--(0,4));

label("$\mathbf{a}$", A, E);
label("$\mathbf{b}$", B, W);
label("$\mathbf{a} + \mathbf{b}$", A + B, N);
label("$\mathbf{0}$", O, SW);
