pair A, B;

A = (1,1);
B = (-3/2,9/4);

real parab (real x) {
  return(x^2);
}

draw(graph(parab,-2,2));
draw((A + (-1,-2))--(A + (1,2)));
draw((A + (1,-0.5))--(A + (-3,1.5)));
