draw((2,0)--(2,2));
draw((4,0)--(4,2));
draw((6,0)--(6,2));
draw((1,2)--(7,2)--(7,4)--(1,4)--cycle);
draw((3,2)--(3,4));
draw((5,2)--(5,4));
