X=G;
Y=F;
draw(1.3*X-.3*Y--1.3*Y-.3*X);

label("$\ell$",1.4*A-.4*B);
label("$k$",1.4*F-.4*G);

//label("$x$",H+(.4,-.15));
label("$30^\circ$",A+(1,-.1));
