draw((0,0,0)--(0,-1,0),dashed);
draw((0,0,0)--(-2,0,0),dashed);

label("$\theta$", (0.7,0.6,0), white);
label("$x$", (2,0,0), SW);
label("$y$", (0,2,0), E);
