[asy]
unitsize(0.3 cm);

pair A, B, C, E, F, G, M;
real x = 4;

B = (0,0);
C = (18,0);
A = intersectionpoint(arc(B,12,0,180),arc(C,16,0,180));
M = (B + C)/2;
F = interp(A,B,x/12);
E = interp(A,C,2*x/16);
G = extension(E,F,A,M);

